INSERT INTO `tp_task` VALUES ('201606032106', '14', '012', '2', '&lt;p&gt;&lt;font color=&quot;#000000&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 该系统要求实现图的可视化建立和图的遍历的动态演示，主要模块包括图中节点的建立，弧（边）的建立，节点的插入和删除动态演示，图的深度优先遍历和广度优先遍历的动态演示，支持图的自动销毁和建立，以及图的存储结构的直观演示。&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;要求界面简洁美观，使用简单易操作，功能完善。&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;编程实现客户端系统，采用&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;C&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;或者&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;java&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;编程工具实现相关模块的编程。&lt;/font&gt;&lt;/p&gt;', '&lt;p&gt;&lt;font color=&quot;#000000&quot;&gt;1. &lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;要求查阅相关&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;开发语言、数据结构相关资料。&lt;/font&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;font color=&quot;#000000&quot;&gt;2. &lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;检索相关文献资料，翻译有关数据结构的一些英文资料一篇。&lt;/font&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;font color=&quot;#000000&quot;&gt;3. &lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;论文撰写要求：&lt;/font&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;font color=&quot;#000000&quot;&gt;（&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;1&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;）要求提交功能完善，可操作性强的成型软件，并对软件进行功能测试；&lt;/font&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;font color=&quot;#000000&quot;&gt;（&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;2&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;） 提交完整的毕业设计说明书，并提供核心程序代码。&lt;/font&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;font color=&quot;#000000&quot;&gt;（&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;3&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;）外文文献及翻译&lt;/font&gt;&lt;/p&gt;', '&lt;p&gt;软件能够演示插入节点、删除节点、图的存储结构的直观演示并实现图的遍历动态演示过程。&lt;br&gt;&lt;/p&gt;', '&lt;p&gt;&lt;font color=&quot;#000000&quot;&gt;3&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;月&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;3&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;日 &lt;/font&gt;&lt;b&gt;&lt;font color=&quot;#000000&quot;&gt;~&lt;/font&gt;&lt;/b&gt;&lt;font color=&quot;#000000&quot;&gt; 3 &lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;月&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt; 31&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;日&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;&amp;nbsp;&amp;nbsp; &lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;熟悉设计内容，查阅相关文献，撰写开题报告。&lt;/font&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;font color=&quot;#000000&quot;&gt;4&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;月&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;1&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;日 &lt;/font&gt;&lt;b&gt;&lt;font color=&quot;#000000&quot;&gt;~&lt;/font&gt;&lt;/b&gt;&lt;font color=&quot;#000000&quot;&gt; 4&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;月&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;15&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;日&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;课题详细调研，整理需求分析及详细设计。&lt;/font&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;font color=&quot;#000000&quot;&gt;4&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;月&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;16&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;日&lt;/font&gt;&lt;b&gt;&lt;font color=&quot;#000000&quot;&gt;~&lt;/font&gt;&lt;/b&gt;&lt;font color=&quot;#000000&quot;&gt; 5&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;月&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;15&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;日&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;软件设计开发。 &lt;/font&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;font color=&quot;#000000&quot;&gt;5&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;月&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;16&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;日 &lt;/font&gt;&lt;b&gt;&lt;font color=&quot;#000000&quot;&gt;~&lt;/font&gt;&lt;/b&gt;&lt;font color=&quot;#000000&quot;&gt; 5&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;月&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;30&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;日&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;毕业设计论文撰写。&lt;/font&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;font color=&quot;#000000&quot;&gt;6&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;月&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;1&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;日 &lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;~6&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;月&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;10&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;日&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;经审查合格后打印装订论文&lt;/font&gt;&lt;/p&gt;&lt;p&gt;&lt;font color=&quot;#000000&quot;&gt;6&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;月&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;10&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;日 &lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;~6&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;月&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;15&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;日&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;论文答辩&lt;/font&gt;&lt;/p&gt;', '/Public/Teacher/d2490f048dc3b77a457e3e450ab4eb38/2018-03-05/5a9d35203cb7c.docx', '1', null);
INSERT INTO `tp_task` VALUES ('201406012101', '16', '012', '1', '&lt;ol&gt;&lt;li&gt;&lt;p&gt;学习掌握相关程序开发语言、网络协议知识。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;对系统进行前期调研，做出详细的需求分析，提出总体设计方案功能模块的设计步骤。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;该系统要求实现一个简单的PC与PC，ＰＣ与phone，phone与phone之间的通话功能，利用传输控制协议和互联网协议原理，winsoch以及流式套接字来完成设计，用电脑客户端实现通话功能功能。要求掌握VisualC++6.0，winsock,tcpip协议，网络知识。要求熟悉Visual C++6.0开发环境。&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;要求界面简洁美观，使用简单易操作，功能完善。&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;', '&lt;p&gt;&lt;font color=&quot;#000000&quot;&gt;1. 要求查阅相关&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;开发语言、网络协议等相关资料。&lt;/font&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;font color=&quot;#000000&quot;&gt;2. &lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;检索相关文献资料，翻译有网络的一些英文资料一篇。&lt;/font&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;font color=&quot;#000000&quot;&gt;3. &lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;论文撰写要求：&lt;/font&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;font color=&quot;#000000&quot;&gt;（&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;1&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;）要求提交功能完善，可操作性强的成型软件，并对软件进行功能测试；&lt;/font&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;font color=&quot;#000000&quot;&gt;（&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;2&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;） 提交完整的毕业设计说明书，并提供核心程序代码。&lt;/font&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;font color=&quot;#000000&quot;&gt;（&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;3&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;）外文文献及翻译&lt;/font&gt;&lt;/p&gt;', '&lt;p&gt;能够简单实现PC与PC，ＰＣ与phone，phone与phone之间的通话功能。&lt;br&gt;&lt;/p&gt;', '&lt;p&gt;&lt;font color=&quot;#000000&quot;&gt;3&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;月&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;3&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;日 &lt;/font&gt;&lt;b&gt;&lt;font color=&quot;#000000&quot;&gt;~&lt;/font&gt;&lt;/b&gt;&lt;font color=&quot;#000000&quot;&gt; 3 &lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;月&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt; 31&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;日&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;&amp;nbsp;&amp;nbsp; &lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;熟悉设计内容，查阅相关文献，撰写开题报告。&lt;/font&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;font color=&quot;#000000&quot;&gt;4&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;月&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;1&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;日 &lt;/font&gt;&lt;b&gt;&lt;font color=&quot;#000000&quot;&gt;~&lt;/font&gt;&lt;/b&gt;&lt;font color=&quot;#000000&quot;&gt; 4&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;月&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;15&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;日&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;课题详细调研，整理需求分析及详细设计。&lt;/font&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;font color=&quot;#000000&quot;&gt;4&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;月&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;16&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;日&lt;/font&gt;&lt;b&gt;&lt;font color=&quot;#000000&quot;&gt;~&lt;/font&gt;&lt;/b&gt;&lt;font color=&quot;#000000&quot;&gt; 5&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;月&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;15&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;日&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;软件设计开发。 &lt;/font&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;font color=&quot;#000000&quot;&gt;5&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;月&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;16&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;日 &lt;/font&gt;&lt;b&gt;&lt;font color=&quot;#000000&quot;&gt;~&lt;/font&gt;&lt;/b&gt;&lt;font color=&quot;#000000&quot;&gt; 5&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;月&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;30&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;日&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;毕业设计论文撰写。&lt;/font&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;font color=&quot;#000000&quot;&gt;6&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;月&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;1&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;日 &lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;~6&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;月&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;10&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;日&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;经审查合格后打印装订论文&lt;/font&gt;&lt;/p&gt;&lt;p&gt;&lt;font color=&quot;#000000&quot;&gt;6&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;月&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;10&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;日 &lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;~6&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;月&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;15&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;日&lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/font&gt;&lt;font color=&quot;#000000&quot;&gt;论文答辩&lt;/font&gt;&lt;/p&gt;', '/Public/Teacher/d2490f048dc3b77a457e3e450ab4eb38/2018-03-05/5a9d371fbc292.docx', '1', null);
